  {
    name: "Pro",
    price: "$99",
    features: ["Unlimited Projects", "100GB Storage", "Priority Support"],
    popular: true
  }
];

<div className="grid grid-cols-1 md:grid-cols-2 gap-8">
  {pricingPlans.map((plan, index) => (
    <Card key={index} className={plan.popular ? 'border-primary' : ''}>
