// Compact Contact Form
<CompactContactForm
  onSubmit={async (formData: ContactFormData) => {
    // Handle submission
  }}
/>

// Minimal Contact Form
<MinimalContactForm
  onSubmit={async (formData: ContactFormData) => {
    // Handle submission
  }}
/>

// Support Contact Form
<SupportContactForm
  onSubmit={async (formData: ContactFormData) => {
    // Handle submission
  }}
/>

// Custom Configuration
<ContactForm
  title="Custom Contact Form"
  description="Tailored form for specific needs"
  variant="default"
  subjects={[
    "Sales Inquiry",
    "Technical Support",
    "Partnership",
    "General Question"
  ]}
  showCompany={true}
  onSubmit={async (formData: ContactFormData) => {
    try {
      const response = await fetch('/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        alert('Message sent successfully!');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  }}
/>

// Variants
// - default: Full featured form with card wrapper
// - compact: Smaller form with reduced spacing
// - minimal: Clean form without card wrapper

// Props
// - title: Form title
// - description: Form description
// - onSubmit: Form submission handler
// - subjects: Array of subject options
// - showCompany: Whether to show company field
// - variant: Form layout variant
// - className: Additional CSS classes
